import React, { Component } from "react";
import ImgMediaCard2 from "./card2";
import "./style.css";
import { getDetails } from "./cardDetails";
import TextField from "@material-ui/core/TextField";

class Cards extends Component {
  state = {
    cards: [],
    filter: "",
  };

  componentDidMount() {
    this.setState({ cards: getDetails() });
  }
  getData = (e) => {
    console.log(e.target.value);
    this.setState({ filter: e.target.value });
  };
  render() {
    return (
      <React.Fragment>
        <div className="d-flex justify-content-end Cards">
          <TextField
            id="standard-basic"
            label="Search"
            color="secondary"
            onChange={this.getData}
          />
        </div>
        <div className="d-flex flex-wrap justify-content-center Cards p-lg-5">
          {this.state.cards
            .filter(
              (card) =>
                card.title
                  .toLowerCase()
                  .includes(this.state.filter.toLowerCase()) ||
                card.description
                  .toLowerCase()
                  .includes(this.state.filter.toLowerCase())
            )
            .map((card) => (
              <div>
                <ImgMediaCard2
                  className="d-flex flex-wrap"
                  key={card.id}
                  card={card}
                />
              </div>
            ))}
        </div>
      </React.Fragment>
    );
  }
}

export default Cards;
